import React from 'react'
import Container from 'react-bootstrap/esm/Container'
import Form from 'react-bootstrap/Form';
import Card from 'react-bootstrap/Card';
import Button from 'react-bootstrap/esm/Button';
import Row from 'react-bootstrap/Row';
import { NavLink, useLocation } from 'react-router-dom';
import { LOGIN_ROUTE, REGISTRATION_ROUTE } from '../utils/consts';




const Auth = () => {
    const location = useLocation()
    const isLogin = location.pathname === LOGIN_ROUTE

  return (
    <Container 
        style={{height: window.innerHeight - 54}}
        className='d-flex justify-content-center align-items-center'>

            <Card style={{width: 600, padding: 20}}>
                <h2 className='m-auto'>{isLogin ? 'Registration' : 'Authorization'}</h2>

                <Form className='d-flex row-gap-4 flex-column'>
                    <Form.Control className='' placeholder='Enter email'></Form.Control>
                    <Form.Control className='' placeholder='Enter password'></Form.Control>
                    <div className='d-flex align-items-center flex-row justify-content-between'>
                        <div  >
                            You haven`t account? <NavLink to={REGISTRATION_ROUTE} >Registration!!!</NavLink>
                        </div>
                        <Button variant='outline-success' className='px-4 align-self-end mt-3'> Login</Button>
                    </div>
                </Form>
            </Card>

    </Container>
  )
}

export default Auth
