import React, { useContext } from 'react'
import { Context } from '..'
import Container from 'react-bootstrap/Container';
import Nav from 'react-bootstrap/Nav';
import Navbar from 'react-bootstrap/Navbar';
import { Link, NavLink } from 'react-router-dom';
import Shop from '../pages/Shop';
import { SHOP_ROUTE } from '../utils/consts';
import Button from 'react-bootstrap/Button';
import {observer} from 'mobx-react-lite'


const AppNavbar =  () => {
    const {user} = useContext(Context)
  return (
    <div>
      <>
      <Navbar className='p-5' bg="dark" data-bs-theme="dark">
        <Container>
            <Link style={{color: 'white', fontWeight : 700, fontSize : 20}} to={SHOP_ROUTE}> Buy device</Link>

            {
                user.isAuth?
                <Nav className="ml-auto gap-5  " style={{color : 'white'}}>
                    <Button variant='outline-light'>Admin panel</Button>
                    <Button variant='outline-light' onClick={()=>user.setIsAuth(false)}>Log out</Button>
                </Nav>
                :
                <Nav className="ml-auto" style={{color : 'white'}}>
                    <Button onClick={()=>user.setIsAuth(true)} variant='outline-light'>Authorthation</Button>
                </Nav>
            }

        </Container>
      </Navbar>

    </>
    </div>
  )
}

export default AppNavbar
