import React, { useContext, useState } from 'react'
import Button from 'react-bootstrap/Button';
import Modal from 'react-bootstrap/Modal'
import Form from 'react-bootstrap/Form';
import Dropdown from 'react-bootstrap/Dropdown';
import DropdownButton from 'react-bootstrap/DropdownButton';
import { Context } from '../..';
import Row from 'react-bootstrap/esm/Row';
import Col from 'react-bootstrap/esm/Col';
const DeviceModal = ({show,onHide}) => {

    const {device} = useContext(Context)
    const [info, setInfo] = useState([])
    const addInfo = ()=>{
        setInfo([...info, {title: '', description : '', number : Date.now()}])
    }
    const removeInfo = (number)=>{
        setInfo(info.filter(item=>item.number !== number))
    }

    const [name, setName] = useState('')
    const [price, setPrice] = useState(0)
    const [file, setFile] = useState(null)

  return (
    <Modal
    show={show}
    onHide={onHide}
    size="lg"
    aria-labelledby="contained-modal-title-vcenter"
    centered
    >
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
                Add Device
            </Modal.Title>
        </Modal.Header>

        <Modal.Body className='d-flex flex-column row-gap-3' >
            <Dropdown>
                <Dropdown.Toggle>Choose type</Dropdown.Toggle>
                <Dropdown.Menu>
                    {
                        device.types.map(type=>
                            <Dropdown.Item key={type.id}>{type.name}</Dropdown.Item>
                        )
                    }
                </Dropdown.Menu>

            </Dropdown>

            <Dropdown>
                <Dropdown.Toggle>Choose brand</Dropdown.Toggle>
                    <Dropdown.Menu>
                        {
                            device.brand.map(brand=>
                                <Dropdown.Item key={brand.id}>{brand.name}</Dropdown.Item>
                            )
                        }
                    </Dropdown.Menu>
            </Dropdown>

            <Form.Control placeholder='Enter device name'/> 
            <Form.Control type='number' placeholder='Enter device price'/> 
            <Form.Control type='file'/> 
            <hr />
            <Button onClick={addInfo} variant='outline-dark'>
                Add new device 
            </Button>
            {
                info.map((item, i)=>
                    <Row key={i}>
                        <Col md={4}>
                            <Form.Control placeholder='Enter name utilities'/>
                        </Col>
                        <Col md={4}>
                            <Form.Control placeholder='Enter description utilities'/>
                        </Col>
                        <Col md={4}>
                            <Button onClick={()=>removeInfo(item.number)} >Delete</Button>
                        </Col>

                    </Row>
                )
            }
        </Modal.Body>

        <Modal.Footer>
            <Button variant='success' onClick={onHide}>Close</Button>
            <Button variant='success' onClick={onHide}>Add Device</Button>
        </Modal.Footer>
    </Modal>
  )
}

export default DeviceModal
